function loadAccordion(target){$(target+' .accordion-button').click(function() {$(target+' .accordion-button').removeClass('on'); $(target+' .accordion-content').slideUp(500); if($(this).next().is(':hidden') == true) {$(this).addClass('on'); $(this).next().slideDown(500); var getSectionId = $(this).parents('.section').attr('id'); var panelScroll = $('.section#'+getSectionId+' '+target+'.scroll.scroll-content'); var buttonScroll = $('.section#'+getSectionId+' '+target+'.scroll.scroll-content .accordion-button.on'); var getPopupId = $(this).parents('.popup').attr('id'); var panelPopupScroll = $('.popup#'+getPopupId+' .scroll.scroll-content'); var buttonPopupScroll = $('.popup#'+getPopupId+' .scroll.scroll-content .accordion-button.on'); if($('.section#'+getSectionId+' '+target+'.scroll.scroll-content .accordion-button').length > 0){setTimeout(function(){$(panelScroll).animate({scrollTop: $(buttonScroll).offset().top - panelScroll.offset().top + panelScroll.scrollTop() }, 500); }, 500); } else if($('.popup#'+getPopupId+' .scroll.scroll-content .accordion-button').length > 0){setTimeout(function(){$(panelPopupScroll).animate({scrollTop: $(buttonPopupScroll).offset().top - panelPopupScroll.offset().top + panelPopupScroll.scrollTop() }, 500); }, 500); } } }); $('.accordion-button').mouseover(function() {$(this).addClass('over'); }).mouseout(function() {$(this).removeClass('over'); }); $('.accordion-content').hide(); }